export default function Orcamento() {
  const navigation = useNavigation();

  const { orcamento, setOrcamento } = useOrcamentoContext();
  const { clienteOrcamento, setClienteOrcamento } = useClienteOrcamentoContext();

  const [modalVisibleNome, setModalVisibleNome] = useState(false);
  const [modalVisibleValor, setModalVisibleValor] = useState(false);
  const [modalVisibleAddMaterial, setModalVisibleAddMaterial] = useState(false);
  const [modalVisibleAddCliente, setModalVisibleAddCliente] = useState(false);
  const [indexDoItemAEditar, setIndexDoItemAEditar] = useState(null);

  const [total, setTotal] = useState(0);


  const editarValor = (index) => {
    setIndexDoItemAEditar(index);
    setModalVisibleValor(true);
  };

  const editarNome = (index) => {
    setIndexDoItemAEditar(index);
    setModalVisibleNome(true);
  };

  const removerItem = (indexToRemove) => {
    // Criar um novo array excluindo o item com o Ã­ndice indexToRemove
    const novoArray = orcamento.filter(
      (item, index) => index !== indexToRemove
    );

    // Atualizar o estado com o novo array
    setOrcamento(novoArray);
  };



  const confirmarApagarItem = (indexToRemove) => {
    Alert.alert("", "Deseja apagar o item da lista?", [
      { text: "NÃ£o", onPress: () => console.log("Cancelado ExclusÃ£o") },
      { text: "Sim", onPress: () => removerItem(indexToRemove) },
    ]);
  };

  const confirmarApagarOrcamento = (index) => {
    Alert.alert("", "Deseja apagar todos os itens do orcamento?", [
      { text: "NÃ£o", onPress: () => console.log("Cancelada ExclusÃ£o") },
      {
        text: "Sim", onPress: () => {
          setOrcamento([]);
          setClienteOrcamento([])
        }
      },
    ]);
  };

  const handleIncrement = (index) => {
    setOrcamento((prevOrcamento) => {
      const novoOrcamento = [...prevOrcamento];
      novoOrcamento[index].quantidade += 1;
      // Garante que a quantidade mÃ­nima seja 1
      novoOrcamento[index].quantidade = Math.max(
        novoOrcamento[index].quantidade,
        1
      );
      return novoOrcamento;
    });
  };

  const handleDecrement = (index) => {
    setOrcamento((prevOrcamento) => {
      const novoOrcamento = [...prevOrcamento];
      novoOrcamento[index].quantidade = Math.max(
        novoOrcamento[index].quantidade - 1,
        1
      );
      return novoOrcamento;
    });
  };

  const calcularTotalOrcamento = () => {
    let novoTotal = 0;

    for (const item of orcamento) {
      // Certifique-se de que o item tem as propriedades valor e quantidade
      if (item.valor !== undefined && item.quantidade !== undefined) {
        novoTotal += item.valor * item.quantidade;
      }
    }
    setTotal(novoTotal);
    return novoTotal;
  };

  useEffect(() => {
    // Chame a funÃ§Ã£o para calcular o total sempre que o orcamento for alterado
    calcularTotalOrcamento();
  }, [orcamento]);

  const renderItem = ({ item, index }) => (
    <View style={styles.listaContainer}>
      {/* Nome Produto + valor */}
      <View
        style={styles.superior}
      >
        <TouchableOpacity onPress={() => editarNome(index)} style={styles.nomeProduto}>
          {item.produto && item.produto.trim() !== "" && (
            <Text style={styles.textLista}>
              {index + 1} - {item.produto}
            </Text>
          )}
        </TouchableOpacity>
        <View style={styles.unidadeProduto}>
          <View>
            <Text style={styles.textUnidade}>Unidade</Text>
          </View>
          <TouchableOpacity
            onPress={() => editarValor(index)}
          >
            <Text style={styles.textUnidade}>
              R$
              {((Number(item.valor) || 0) * 1).toLocaleString("pt-BR", {
                minimumFractionDigits: 2,
                maximumFractionDigits: 2,
              })}
            </Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Apagar produto + quantidade + valor final*/}
      <View style={styles.inferior}>
        <View style={styles.editarProduto}>
          <TouchableOpacity
            style={{ alignSelf: "center" }}
            onPress={() => confirmarApagarItem(index)}
          >
            <MaterialIcons name="close" size={30} color="red" />
          </TouchableOpacity>

        </View>

        <View style={styles.quantidade}>
          <TouchableOpacity
            style={styles.aumentar}
            onPress={() => handleIncrement(index)}
          >
            <AntDesign
              color={"#86c694"}
              size={23}
              name="up"
              style={{ textAlign: "center" }}
            />
          </TouchableOpacity>

          <View style={styles.multiplicar}>
            <Text style={styles.textMultiplicar}>
              {orcamento[index].quantidade || 1}
            </Text>
          </View>

          <TouchableOpacity
            style={styles.diminuir}
            onPress={() => handleDecrement(index)}
          >
            <AntDesign
              size={23}
              color={"#86c694"}
              name="down"
              style={{ textAlign: "center" }}
            />
          </TouchableOpacity>
        </View>
        <View style={styles.valorFinal}>
          {item.valor !== undefined && (
            <View>
              <View>
                <Text style={styles.textTotal}>
                  R$
                  {(
                    item.valor * (orcamento[index].quantidade || 1)
                  ).toLocaleString("pt-BR", {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}
                </Text>
              </View>
            </View>
          )}
        </View>
      </View>
    </View>
  );

  const gerarPDF = async () => {
    try {
      const htmlString = OrcamentoHtml({ orcamento });
      const file = await Print.printToFileAsync({
        html: htmlString,
        base64: false,
      });
      // DiretÃ³rio onde vocÃª quer salvar o PDF
      const pastaOrcamentosSalvos = `${FileSystem.documentDirectory}assets/`;

      // Garantir que o diretÃ³rio exista
      await FileSystem.makeDirectoryAsync(pastaOrcamentosSalvos, {
        intermediates: true,
      });

      // Caminho para o arquivo no diretÃ³rio especÃ­fico
      const caminhoNoDiretorio = `${pastaOrcamentosSalvos}OrÃ§amento.pdf`;

      // Mover o arquivo para o diretÃ³rio especÃ­fico
      await FileSystem.moveAsync({
        from: file.uri,
        to: caminhoNoDiretorio,
      });
      // Exibir o visualizador de PDF
      await Sharing.shareAsync(caminhoNoDiretorio);
    } catch (error) {
      console.error("Erro ao gerar e compartilhar o PDF:", error);
    }
  };

  return (
    <View>

      <View
        style={{
          width: "90%",
          marginLeft: 10,
        }}
      >
        <View style={{ flexDirection: "row" }}>
          <Text style={{ fontSize: 15 }}>Cliente:</Text>
          <Text style={{ fontSize: 15, fontWeight: "bold" }}>
            {" "}
            {clienteOrcamento.cliente}
          </Text>
        </View>
        <View style={{ flexDirection: "row" }}>
          <Text style={{ fontSize: 15 }}>Telefone:</Text>
          <Text style={{ fontSize: 15, fontWeight: "bold" }}>
            {" "}
            {clienteOrcamento.telefone}
          </Text>
        </View>
        <View style={{ flexDirection: "row" }}>
          <Text style={{ fontSize: 15 }}>EndereÃ§o:</Text>
          <Text style={{ fontSize: 15, fontWeight: "bold" }}>
            {" "}
            {clienteOrcamento.endereco}
          </Text>
        </View>
      </View>
      <View style={styles.container}>
        <FlatList
          data={orcamento}
          renderItem={renderItem}
          keyExtractor={(item, index) => index.toString()}
          numColumns={1} // Configura o nÃºmero de colunas
        />
      </View>
      <View style={styles.resumo}>
        <View style={styles.resumoContent}>
          <Text style={styles.textText}>TOTAL</Text>
          <Text style={[styles.textTotal, { fontSize: 30 }]}>
            R${" "}
            {total.toLocaleString("pt-BR", {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}
          </Text>
        </View>
      </View>


      <View style={styles.buttonContainer}>
        <View style={{ flexDirection: "row", justifyContent: "space-between" }}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => setModalVisibleAddCliente(true)}
          >
            <Text style={styles.buttonText}>Adicionar Cliente</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.button}
            onPress={() => setModalVisibleAddMaterial(true)}
          >
            <Text style={styles.buttonText}>Adicionar Material</Text>
          </TouchableOpacity>
        </View>
        <View style={{ flexDirection: "row", justifyContent: "space-between" }}>
          <TouchableOpacity style={styles.button} onPress={() => gerarPDF()}>
            <Text style={styles.buttonText}>Gerar PDF</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.button}
            onPress={() => confirmarApagarOrcamento()}
          >
            <Text style={styles.buttonText}>Limpar Lista</Text>
          </TouchableOpacity>
        </View>
      </View>

      <Modal
        visible={modalVisibleNome}
        animationType="fade"
        transparent={true}
      >
        <ModalEditarNome
          handleClose={() => setModalVisibleNome(false)}

          indexDoItemAEditar={indexDoItemAEditar}
        />
      </Modal>
      <Modal
        visible={modalVisibleValor}
        animationType="fade"
        transparent={true}
      >
        <ModalEditarValor
          handleClose={() => setModalVisibleValor(false)}

          indexDoItemAEditar={indexDoItemAEditar}
        />
      </Modal>
      <Modal
        visible={modalVisibleAddCliente}
        animationType="fade"
        transparent={true}
      >
        <ModalAdicionarCliente
          handleClose={() => setModalVisibleAddCliente(false)}
          tipo={"Orcamento"}
        />
      </Modal>
      <Modal
        visible={modalVisibleAddMaterial}
        animationType="fade"
        transparent={true}
      >
        <ModalAdicionarMaterial
          handleClose={() => setModalVisibleAddMaterial(false)}
          tipo={'Orcamento'}
        />
      </Modal>
    </View>
  );
}

